unit.struct:
struct mxs_charset_entry
{
    const char* name;
    size_t      len;
};

function.block:
{
    const struct mxs_charset_entry* key = (const struct mxs_charset_entry*)l;
    const struct mxs_charset_entry* value = (const struct mxs_charset_entry*)r;

    int rv = strncasecmp(key->name, value->name, MIN(key->len, value->len));

    if (key->len != value->len)
    {
        if (rv == 0)
        {
            rv = key->len < value->len ? -1 : 1;
        }
    }

    return rv;
}

{
    struct mxs_charset_entry key = { p, n };

    return bsearch(&key,
                   mxs_charset_names, N_MXS_CHARSET_NAMES, sizeof(mxs_charset_names[0]),
                   mxs_compare_charset_names) != 0;
}

{
#else
int sqlite3GetToken(const unsigned char *z, int *tokenType){
#endif
  int i, c;
  switch( aiClass[*z] ){  /* Switch on the character-class of the first byte
                          ** of the token. See the comment on the CC_ defines
                          ** above. */
    case CC_SPACE: {
      testcase( z[0]==' ' );
      testcase( z[0]=='\t' );
      testcase( z[0]=='\n' );
      testcase( z[0]=='\f' );
      testcase( z[0]=='\r' );
      for(i=1; sqlite3Isspace(z[i]); i++){}
      *tokenType = TK_SPACE;
      return i;
    }
    case CC_MINUS: {
      if( z[1]=='-' ){
#ifdef MAXSCALE
        maxscaleComment();
#endif
        for(i=2; (c=z[i])!=0 && c!='\n'; i++){}
        *tokenType = TK_SPACE;   /* IMP: R-22934-25134 */
        return i;
      }
      *tokenType = TK_MINUS;
      return 1;
    }
    case CC_LP: {
      *tokenType = TK_LP;
      return 1;
    }
    case CC_RP: {
      *tokenType = TK_RP;
      return 1;
    }
    case CC_SEMI: {
      *tokenType = TK_SEMI;
      return 1;
    }
    case CC_PLUS: {
      *tokenType = TK_PLUS;
      return 1;
    }
    case CC_STAR: {
      *tokenType = TK_STAR;
      return 1;
    }
    case CC_SLASH: {
      if( z[1]!='*' || z[2]==0 ){
        *tokenType = TK_SLASH;
        return 1;
      }
#ifdef MAXSCALE
      if ( z[2]=='!' || (z[2]=='M' && z[3]=='!')){
        int j = (z[2]=='M' ? 4 : 3);
        // MySQL-specific code
        for (i=j, c=z[j-1]; (c!='*' || z[i]!='/') && (c=z[i])!=0; i++){}
        if (c=='*' && z[i]=='/'){
          if (sqlite3Isdigit(z[j])) {
            // A version specific executable comment,
            // e.g. "/*!99999 ..." or "/*M!99999 ..." => never parsed.
            extern void maxscaleSetStatusCap(int);
            maxscaleSetStatusCap(2); // QC_QUERY_PARTIALLY_PARSED, see query_classifier.h:qc_parse_result
            ++i; // Next after the trailing '/'
          }
          else {
            // A non-version specific executable comment,
            // e.g."/*! select 1 */ or "/*M! select 1 */ => always parsed.
            char* znc = (char*) z;
            znc[0]=znc[1]=znc[2]=znc[i-1]=znc[i]=' '; // Remove comment chars, i.e. "/*!" and "*/".
            if (j==4){
              znc[3]=0; // It wasn't "/*!" but "/*M!".
            }
            for (i=j; sqlite3Isspace(z[i]); ++i){} // Jump over any space.
          }
        }
      } else {
        for(i=3, c=z[2]; (c!='*' || z[i]!='/') && (c=z[i])!=0; i++){}

        if( c ) i++;
      }
#else
      for(i=3, c=z[2]; (c!='*' || z[i]!='/') && (c=z[i])!=0; i++){}
      if( c ) i++;
#endif
      *tokenType = TK_SPACE;   /* IMP: R-22934-25134 */
      return i;
    }
    case CC_PERCENT: {
      *tokenType = TK_REM;
      return 1;
    }
    case CC_EQ: {
      *tokenType = TK_EQ;
      return 1 + (z[1]=='=');
    }
    case CC_LT: {
      if( (c=z[1])=='=' ){
#ifdef MAXSCALE
        if ( z[2]=='>' ){
          // "<=>"
          *tokenType = TK_EQ;
          return 3;
        }
#endif
        *tokenType = TK_LE;
        return 2;
      }else if( c=='>' ){
        *tokenType = TK_NE;
        return 2;
      }else if( c=='<' ){
        *tokenType = TK_LSHIFT;
        return 2;
      }else{
        *tokenType = TK_LT;
        return 1;
      }
    }
    case CC_GT: {
      if( (c=z[1])=='=' ){
        *tokenType = TK_GE;
        return 2;
      }else if( c=='>' ){
        *tokenType = TK_RSHIFT;
        return 2;
      }else{
        *tokenType = TK_GT;
        return 1;
      }
    }
    case CC_BANG: {
      if( z[1]!='=' ){
#ifdef MAXSCALE
        *tokenType = TK_NOT;
        return 1;
#else
        *tokenType = TK_ILLEGAL;
        return 2;
#endif
      }else{
        *tokenType = TK_NE;
        return 2;
      }
    }
    case CC_PIPE: {
      if( z[1]!='|' ){
        *tokenType = TK_BITOR;
        return 1;
      }else{
        *tokenType = TK_CONCAT;
        return 2;
      }
    }
    case CC_COMMA: {
      *tokenType = TK_COMMA;
      return 1;
    }
    case CC_AND: {
      *tokenType = TK_BITAND;
      return 1;
    }
    case CC_TILDA: {
      *tokenType = TK_BITNOT;
      return 1;
    }
    case CC_QUOTE: {
      int delim = z[0];
      testcase( delim=='`' );
      testcase( delim=='\'' );
      testcase( delim=='"' );
      for(i=1; (c=z[i])!=0; i++){
        if( c==delim ){
          if( z[i+1]==delim ){
            i++;
          }else{
            break;
          }
#ifdef MAXSCALE
        }else if (c == '\\' ){
          if ( z[i+1] ){
            i++;
          }
#endif
        }
      }
#ifdef MAXSCALE
      if( c=='\'' || c=='"' ){
#else
      if( c=='\'' ){
#endif
        *tokenType = TK_STRING;
        return i+1;
      }else if( c!=0 ){
        *tokenType = TK_ID;
        return i+1;
      }else{
        *tokenType = TK_ILLEGAL;
        return i;
      }
    }
    case CC_DOT: {
#ifndef SQLITE_OMIT_FLOATING_POINT
      if( !sqlite3Isdigit(z[1]) )
#endif
      {
        *tokenType = TK_DOT;
        return 1;
      }
#ifdef MAXSCALE
      /* Next char is a digit, we need to sniff further to find out whether it
      ** is an identifer that starts with a digit.
      */
      int j=1;
      int nondigitChars=0;
      while ( IdChar(z[j]) ){
        if ( !sqlite3Isdigit(z[j++]) ){
          ++nondigitChars;
        }
      }
      if ( nondigitChars ){
        // At least one char that is not a digit => an id (and not a float) coming.
        *tokenType = TK_DOT;
        return 1;
      }
#endif
      /* If the next character is a digit, this is a floating point
      ** number that begins with ".".  Fall thru into the next case */
    }
    case CC_DIGIT: {
      testcase( z[0]=='0' );  testcase( z[0]=='1' );  testcase( z[0]=='2' );
      testcase( z[0]=='3' );  testcase( z[0]=='4' );  testcase( z[0]=='5' );
      testcase( z[0]=='6' );  testcase( z[0]=='7' );  testcase( z[0]=='8' );
      testcase( z[0]=='9' );
      *tokenType = TK_INTEGER;
#ifndef SQLITE_OMIT_HEX_INTEGER
      if( z[0]=='0' && (z[1]=='x' || z[1]=='X') && sqlite3Isxdigit(z[2]) ){
        for(i=3; sqlite3Isxdigit(z[i]); i++){}
        return i;
      }
#endif
      for(i=0; sqlite3Isdigit(z[i]); i++){}
#ifndef SQLITE_OMIT_FLOATING_POINT
      if( z[i]=='.' ){
        i++;
        while( sqlite3Isdigit(z[i]) ){ i++; }
        *tokenType = TK_FLOAT;
      }
      if( (z[i]=='e' || z[i]=='E') &&
           ( sqlite3Isdigit(z[i+1]) 
            || ((z[i+1]=='+' || z[i+1]=='-') && sqlite3Isdigit(z[i+2]))
           )
      ){
        i += 2;
        while( sqlite3Isdigit(z[i]) ){ i++; }
        *tokenType = TK_FLOAT;
      }
#endif
      while( IdChar(z[i]) ){
#ifdef MAXSCALE
        *tokenType = TK_ID;
#else
        *tokenType = TK_ILLEGAL;
#endif
        i++;
      }
      return i;
    }
    case CC_QUOTE2: {
      for(i=1, c=z[0]; c!=']' && (c=z[i])!=0; i++){}
      *tokenType = c==']' ? TK_ID : TK_ILLEGAL;
      return i;
    }
    case CC_VARNUM: {
      *tokenType = TK_VARIABLE;
      for(i=1; sqlite3Isdigit(z[i]); i++){}
      return i;
    }
    case CC_DOLLAR:
    case CC_VARALPHA: {
      int n = 0;
      testcase( z[0]=='$' );  testcase( z[0]=='@' );
      testcase( z[0]==':' );  testcase( z[0]=='#' );
#ifdef MAXSCALE
      if (z[0]=='#') {
        if (maxscaleComment()) {
          for(i=1; (c=z[i])!=0 && c!='\n'; i++){}
          *tokenType = TK_SPACE;
          return i;
        }
      }
      if (z[0]==':' && z[1]=='=') {
        *tokenType = TK_EQ;
        return 2;
      }
#endif
      *tokenType = TK_VARIABLE;
      for(i=1; (c=z[i])!=0; i++){
#ifdef MAXSCALE
          if ( (i == 1) && (z[0] == '@') && (c == '@') ) {
          // If the first char is '@' then if the second char is a '@'
          // it is a system variable (@@xyz).
          continue;
        }else if( IdChar(c) ){
#else
        if( IdChar(c) ){
#endif
          n++;
#ifndef SQLITE_OMIT_TCL_VARIABLE
        }else if( c=='(' && n>0 ){
          do{
            i++;
          }while( (c=z[i])!=0 && !sqlite3Isspace(c) && c!=')' );
          if( c==')' ){
            i++;
          }else{
            *tokenType = TK_ILLEGAL;
          }
          break;
        }else if( c==':' && z[i+1]==':' ){
          i++;
#endif
#ifdef MAXSCALE
        }else if ( c=='\'' || c=='"' || c=='`' ){
          int q=c;
          ++i;
          while ( IdChar(z[i]) ) {
            ++i;
            ++n;
          }
          if ( z[i]==q )
          {
            ++i;
            break;
          }
#endif
        }else{
          break;
        }
      }
      if( n==0 ) *tokenType = TK_ILLEGAL;
      return i;
    }
    case CC_KYWD: {
#ifdef MAXSCALE
      // This is for bit fields, e.g. b'10110111'.
      if ( z[0]=='b' || z[0]=='B' ) {
        if ( z[1]=='\'' ){
          // We return it as an integer. We are not interested in the value
          *tokenType = TK_INTEGER;
          for(i=2; (z[i]=='0'||z[i]=='1'); i++){}
          if ( z[i]!='\'' ){
            *tokenType = TK_ILLEGAL;
            while ( z[i] && z[i]!='\'' ){ i++; }
          }
          if ( z[i] ) i++;
          return i;
        }
      }
      /* Not a bit field. It may be a keyword so we flow through */
#endif
      for(i=1; aiClass[z[i]]<=CC_KYWD; i++){}
#ifdef MAXSCALE
      if ( z[0]== '_' ) {
        // This can be a case of [_charset_name], so we need to
        // accept more. We can eat all characters acceptable for
        // an identifier.
        while ( IdChar(z[i]) ) { i++; }

        if (mxs_is_charset_name((char*)z + 1, i - 1)) {
            *tokenType = TK_CHARSET_NAME_KW;
            return i;
        } else {
            // Token type will be TK_ID.
            break;
        }
      }
#endif
      if( IdChar(z[i]) ){
        /* This token started out using characters that can appear in keywords,
        ** but z[i] is a character not allowed within keywords, so this must
        ** be an identifier instead */
        i++;
        break;
      }
      *tokenType = TK_ID;
#ifdef MAXSCALE
      i = keywordCode((char*)z, i, tokenType);
      if (*tokenType != TK_ID)
      {
        if (pParse) {
          if (z != (const unsigned char *)pParse->zTail) {
            const char *p = (const char*)z - 1;
            while ((p != pParse->zTail) && sqlite3Isspace(*p) && *p != '\n') {
              --p;
            }

            if (*p == '.') {
              /* If the last character before the keyword is '.' then
              ** we assume this token is the second part of a qualified
              ** name, e.g. "tbl1.index" in which case we return the
              ** keyword as an id.
              */
              *tokenType = TK_ID;
            }
          }
        }

        if (*tokenType != TK_ID) {
          extern int maxscaleKeyword(int);
          extern int maxscaleTranslateKeyword(int);

          *tokenType = maxscaleTranslateKeyword(*tokenType);

          if (*tokenType != TK_ID) {
            if (maxscaleKeyword(*tokenType) != 0)
            {
              /* Consume the entire string. */
              while ( z[i] ) {
                ++i;
              }
            }
          }
        }
      }
      return i;
#else
      return keywordCode((char*)z, i, tokenType);
#endif
    }
#ifndef SQLITE_OMIT_BLOB_LITERAL
    case CC_X: {
      testcase( z[0]=='x' ); testcase( z[0]=='X' );
      if( z[1]=='\'' ){
        *tokenType = TK_BLOB;
        for(i=2; sqlite3Isxdigit(z[i]); i++){}
        if( z[i]!='\'' || i%2 ){
          *tokenType = TK_ILLEGAL;
          while( z[i] && z[i]!='\'' ){ i++; }
        }
        if( z[i] ) i++;
        return i;
      }
      /* If it is not a BLOB literal, then it must be an ID, since no
      ** SQL keywords start with the letter 'x'.  Fall through */
    }
#endif
#ifdef MAXSCALE
    // It may be the "XA" keyword.
    // If the next character is 'a' or 'A', followed by whitespace or a
    // comment, then we are indeed dealing with the "XA" keyword.
    if (( z[1]=='a' || z[1]=='A' ) &&
        (sqlite3Isspace(z[2]) ||                              // Whitespace
         (z[2]=='/' && z[3]=='*') ||                          // Beginning of /* comment
         (z[2]=='#') ||                                       // # eol comment
         (z[2]=='-' && z[3]=='-' && sqlite3Isspace(z[4])))) { // --  eol comment
      extern int maxscaleKeyword(int);

      *tokenType = TK_XA;
      maxscaleKeyword(*tokenType);
      return 2;
    }
#endif
    case CC_ID: {
      i = 1;
      break;
    }
    default: {
      *tokenType = TK_ILLEGAL;
      return 1;
    }
  }
  while( IdChar(z[i]) ){ i++; }
  *tokenType = TK_ID;
  return i;
}

/*
** Run the parser on the given SQL string.  The parser structure is
** passed in.  An SQLITE_ status code is returned.  If an error occurs
** then an and attempt is made to write an error message into 
** memory obtained from sqlite3_malloc() and to make *pzErrMsg point to that
** error message.
*/
int sqlite3RunParser(Parse *pParse, const char *zSql, char **pzErrMsg){
  int nErr = 0;                   /* Number of errors encountered */
  int i;                          /* Loop counter */
  void *pEngine;                  /* The LEMON-generated LALR(1) parser */
  int tokenType;                  /* type of the next token */
  int lastTokenParsed = -1;       /* type of the previous token */
  sqlite3 *db = pParse->db;       /* The database connection */
  int mxSqlLen;                   /* Max length of an SQL string */

  assert( zSql!=0 );
  mxSqlLen = db->aLimit[SQLITE_LIMIT_SQL_LENGTH];
  if( db->nVdbeActive==0 ){
    db->u1.isInterrupted = 0;
  }
  pParse->rc = SQLITE_OK;
  pParse->zTail = zSql;
  i = 0;
  assert( pzErrMsg!=0 );
  /* sqlite3ParserTrace(stdout, "parser: "); */
  pEngine = sqlite3ParserAlloc(sqlite3Malloc);
  if( pEngine==0 ){
    sqlite3OomFault(db);
    return SQLITE_NOMEM;
  }
  assert( pParse->pNewTable==0 );
  assert( pParse->pNewTrigger==0 );
  assert( pParse->nVar==0 );
  assert( pParse->nzVar==0 );
  assert( pParse->azVar==0 );
  while( zSql[i]!=0 ){
    assert( i>=0 );
    pParse->sLastToken.z = &zSql[i];
#ifdef MAXSCALE
    pParse->sLastToken.n = sqlite3GetToken(pParse,(unsigned char*)&zSql[i],&tokenType);
#else
    pParse->sLastToken.n = sqlite3GetToken((unsigned char*)&zSql[i],&tokenType);
#endif
    i += pParse->sLastToken.n;
    if( i>mxSqlLen ){
      pParse->rc = SQLITE_TOOBIG;
      break;
    }
    if( tokenType>=TK_SPACE ){
      assert( tokenType==TK_SPACE || tokenType==TK_ILLEGAL );
      if( db->u1.isInterrupted ){
        pParse->rc = SQLITE_INTERRUPT;
        break;
      }
      if( tokenType==TK_ILLEGAL ){
        sqlite3ErrorMsg(pParse, "unrecognized token: \"%T\"",
                        &pParse->sLastToken);
        break;
      }
    }else{
      if( tokenType==TK_SEMI ) pParse->zTail = &zSql[i];
      sqlite3Parser(pEngine, tokenType, pParse->sLastToken, pParse);
      lastTokenParsed = tokenType;
      if( pParse->rc!=SQLITE_OK || db->mallocFailed ) break;
    }
  }
  assert( nErr==0 );
  if( pParse->rc==SQLITE_OK && db->mallocFailed==0 ){
    assert( zSql[i]==0 );
    if( lastTokenParsed!=TK_SEMI ){
      sqlite3Parser(pEngine, TK_SEMI, pParse->sLastToken, pParse);
      pParse->zTail = &zSql[i];
    }
    if( pParse->rc==SQLITE_OK && db->mallocFailed==0 ){
      sqlite3Parser(pEngine, 0, pParse->sLastToken, pParse);
    }
  }
#ifdef YYTRACKMAXSTACKDEPTH
  sqlite3_mutex_enter(sqlite3MallocMutex());
  sqlite3StatusHighwater(SQLITE_STATUS_PARSER_STACK,
      sqlite3ParserStackPeak(pEngine)
  );
  sqlite3_mutex_leave(sqlite3MallocMutex());
#endif /* YYDEBUG */
  sqlite3ParserFree(pEngine, sqlite3_free);
  if( db->mallocFailed ){
    pParse->rc = SQLITE_NOMEM;
  }
  if( pParse->rc!=SQLITE_OK && pParse->rc!=SQLITE_DONE && pParse->zErrMsg==0 ){
    pParse->zErrMsg = sqlite3MPrintf(db, "%s", sqlite3ErrStr(pParse->rc));
  }
  assert( pzErrMsg!=0 );
  if( pParse->zErrMsg ){
    *pzErrMsg = pParse->zErrMsg;
    sqlite3_log(pParse->rc, "%s", *pzErrMsg);
    pParse->zErrMsg = 0;
    nErr++;
  }
  if( pParse->pVdbe && pParse->nErr>0 && pParse->nested==0 ){
    sqlite3VdbeDelete(pParse->pVdbe);
    pParse->pVdbe = 0;
  }
#ifndef SQLITE_OMIT_SHARED_CACHE
  if( pParse->nested==0 ){
    sqlite3DbFree(db, pParse->aTableLock);
    pParse->aTableLock = 0;
    pParse->nTableLock = 0;
  }
#endif
#ifndef SQLITE_OMIT_VIRTUALTABLE
  sqlite3_free(pParse->apVtabLock);
#endif

  if( !IN_DECLARE_VTAB ){
    /* If the pParse->declareVtab flag is set, do not delete any table 
    ** structure built up in pParse->pNewTable. The calling code (see vtab.c)
    ** will take responsibility for freeing the Table structure.
    */
    sqlite3DeleteTable(db, pParse->pNewTable);
  }

  sqlite3WithDelete(db, pParse->pWithToFree);
  sqlite3DeleteTrigger(db, pParse->pNewTrigger);
  for(i=pParse->nzVar-1; i>=0; i--) sqlite3DbFree(db, pParse->azVar[i]);
  sqlite3DbFree(db, pParse->azVar);
  while( pParse->pAinc ){
    AutoincInfo *p = pParse->pAinc;
    pParse->pAinc = p->pNext;
    sqlite3DbFree(db, p);
  }
  while( pParse->pZombieTab ){
    Table *p = pParse->pZombieTab;
    pParse->pZombieTab = p->pNextZombie;
    sqlite3DeleteTable(db, p);
  }
  assert( nErr==0 || pParse->rc!=SQLITE_OK );
  return nErr;
}
