function.block:
{
	if (open_fn == NULL || write_fn == NULL)
		return;

	__open_fn = open_fn;
    __close_fn = close_fn;
	__write_fn = write_fn;
	__msg_ctx = ctx;
}

{
	__open_fn = NULL;
	__close_fn = NULL;
	__write_fn = NULL;
	__msg_ctx = NULL;
}

{
	__pre_write_fn = pre_write;
	__pre_write_ctx = ctx;
}

{
	__stdout_enable = onoff;
}

{
	__trace_enable = onoff;
}

{
	if (fp == NULL || info_pre == NULL || *info_pre == 0)
		return;

	if (__open_fn != NULL) {
		int ret = __open_fn(ACL_VSTREAM_PATH(fp), __msg_ctx);
		/* if return < 0, use the default log */
		if (ret < 0) {
			__open_fn = NULL;
			__close_fn = NULL;
			__write_fn = NULL;
			__msg_ctx = NULL;
			acl_log_fp_set(fp, info_pre);
		}
	} else
		acl_log_fp_set(fp, info_pre);

	__log_open_flag = 1;
}

{
	const char *myname = "acl_msg_open";
	int  ret;

	if (log_file == NULL || *log_file == 0
		|| info_pre == NULL || *info_pre == 0)
	{
		return;
	}

	if (__open_fn != NULL) {
		ret = __open_fn(log_file, __msg_ctx);
		/* if return < 0, use the default log */
		if (ret < 0) {
			__open_fn = NULL;
			__close_fn = NULL;
			__write_fn = NULL;
			__msg_ctx = NULL;
			ret = acl_open_log(log_file, info_pre);
		}
	} else
		ret = acl_open_log(log_file, info_pre);

	if (ret < 0) {
		printf("%s: can't open log file=%s, info_pre=%s,%s\n",
			myname, log_file, info_pre, acl_last_serror());
		__log_open_flag = 0;
		__open_fn = NULL;
		__write_fn = NULL;
		__msg_ctx = NULL;
		return;
	}
	__log_open_flag = 1;
}

{
	if (__log_open_flag == 0)
		return;
	acl_close_log();
	__log_open_flag = 0;
	if (__close_fn)
		__close_fn(__msg_ctx);
}

{
	va_list ap;

	va_start (ap, fmt);

	if (__stdout_enable) {
#if (defined(_WIN32) || defined(_WIN64)) && _MSC_VER < 1900
		vprintf(fmt, ap);
		printf("\r\n");
#else
		va_list ap_tmp;
		va_copy(ap_tmp, ap);
# ifdef LINUX
		printf("acl_msg_info->pid(%d), ", getpid());
# elif defined(SOLARIS)
		printf("acl_msg_info->pid(%ld), ", getpid());
# endif
		vprintf(fmt, ap_tmp);
		printf("\r\n");
#endif
	}

	if (__pre_write_fn) {
		__pre_write_fn(__pre_write_ctx, fmt, ap);
	}

	if (__log_open_flag) {
		if (__write_fn != NULL) {
			__write_fn(__msg_ctx, fmt, ap);
		} else {
			acl_write_to_log2("info", fmt, ap);
		}
	}

	va_end (ap);
}

{
	if (__stdout_enable) {
#if (defined(_WIN32) || defined(_WIN64)) && _MSC_VER < 1900
		vprintf(fmt, ap);
		printf("\r\n");
#else
		va_list ap_tmp;
		va_copy(ap_tmp, ap);
# ifdef LINUX
		printf("acl_msg_info->pid(%d), ", getpid());
# elif defined(SOLARIS)
		printf("acl_msg_info->pid(%ld), ", getpid());
# endif
