#endif
	}

	if (__pre_write_fn) {
		__pre_write_fn(__pre_write_ctx, fmt, ap);
	}

	if (__log_open_flag) {
		if (__write_fn != NULL) {
			__write_fn(__msg_ctx, fmt, ap);
		} else {
			acl_write_to_log2("info", fmt, ap);
		}
	}
}

