	if (__log_open_flag) {
		if (__write_fn != NULL) {
			__write_fn(__msg_ctx, fmt, ap);
		} else {
			acl_write_to_log2("warn", fmt, ap);
		}
	}

	va_end (ap);

	if (__trace_enable) {
		acl_trace_info();
	}
}

