{
	va_list ap;

	va_start (ap, fmt);

	if (__stdout_enable) {
#if (defined(_WIN32) || defined(_WIN64)) && _MSC_VER < 1900
		printf("fatal:");
		vprintf(fmt, ap);
		printf("\r\n");
#else
		va_list ap_tmp;
		va_copy(ap_tmp, ap);
# ifdef LINUX
		printf("acl_msg_fatal->pid(%d), ", getpid());
# elif defined(SOLARIS)
		printf("acl_msg_fatal->pid(%ld), ", getpid());
# endif
		printf("fatal:");
		vprintf(fmt, ap_tmp);
		printf("\r\n");
#endif
	}

	if (__pre_write_fn) {
		__pre_write_fn(__pre_write_ctx, fmt, ap);
	}

	if (__log_open_flag) {
		if (__write_fn != NULL) {
			__write_fn(__msg_ctx, fmt, ap);
		} else {
			acl_write_to_log2("fatal", fmt, ap);
		}
	}

	va_end (ap);
	acl_trace_info();
	acl_close_log();
	abort();
}

{
	if (__stdout_enable) {
#if (defined(_WIN32) || defined(_WIN64)) && _MSC_VER < 1900
		vprintf(fmt, ap);
		printf("\r\n");
#else
		va_list ap_tmp;
		va_copy(ap_tmp, ap);
