{
	va_list ap;

	(void) status;

	va_start (ap, fmt);

	if (__stdout_enable) {
#if (defined(_WIN32) || defined(_WIN64)) && _MSC_VER < 1900
		vprintf(fmt, ap);
		printf("\r\n");
#else
		va_list ap_tmp;
		va_copy(ap_tmp, ap);
# ifdef LINUX
